package com.example.myapplication;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.TextView;import com.daimajia.androidanimations.library.Techniques;import com.daimajia.androidanimations.library.YoYo;public class MainActivity extends AppCompatActivity {    TextView text1;    Button pre,next;    int i=0;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        final Techniques[] tech=Techniques.values();        text1=findViewById(R.id.text);        pre=findViewById(R.id.pre);        next=findViewById(R.id.next);        pre.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                YoYo.with(tech[i]).playOn(text1);                if (i<tech.length-1){                i=i+1;}            }        });        next.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                YoYo.with(tech[i]).playOn(text1);                if (i!=0){                    i=i-1;}            }        });    }}